% write_voffsetx.m% A script to write files containing vertical offsets of mmp% sensors%I've modified the other file in this directory to write the%crg offsets for mmp2s.  It reads in the old voffsets file%and adds offset_mag, saving it with the same name.%mha 9/9/99mmpfolders;%pick an mmp2 dropdrop=7200;mmpid=read_mmpid(drop);[scanid,voffsetid]=read_config_mmp(mmpid,drop);str=['load ' setstr(39) mmpdatabase filesep 'config' filesep mmpid filesep ...	     voffsetid '.mat' setstr(39)];eval(str)  f_str=[mmpdatabase filesep 'config' filesep mmpid filesep ...	     voffsetid '.mat'];offset_crg=offset_v1; offset_i1=offset_v1; offset_i2=offset_v1; offset_i3=offset_v1; offset_i4=offset_v1;     sv_str=['save ' setstr(39) f_str setstr(39) ' offset_a1 ' ...   ' offset_a2 offset_ac offset_csbe ' ...   ' offset_obs offset_tl1 offset_tl2 offset_th1 ' ...   ' offset_th2 offset_tsbe offset_v1 offset_v2 ' ...   ' offset_tl offset_crg offset_i1 offset_i2 offset_i3 offset_i4'];     eval(sv_str)   