% calc_kvh1G_mmp.m - process/retrieve KVH headings for mmp3%	Input: drop,cruise,mmpid,procdata; Output: hdg, magnetic_variationif (drop_flag==1 | drop_flag==2) & strcmp(mmpid,'mmp3')   kv_fil = [procdata filesep cruise filesep 'kvh' filesep 'kvh' int2str(drop) '.mat'];   hdg=[];   magnetic_variation=read_magvar_mmp(cruise);   if drop_flag==2 & exist(kv_fil)==2      load(kv_fil); % retrieve kvh data   else      hdg=kvh1_mmp(drop); % compute from raw data file      hdg=magnetic_variation+hdg;      save(kv_fil, 'hdg');   end      clear kv_fil  end