function [scanlen,byteoffsets]=byteoffsets1_mmp(scanid,scanpos)% byteoffsets_mmp%   Usage: byteoffsets=byteoffsets_mmp(scanid,scanpos)%      scanid is the id string for the set of bytes in a data %         channel.%      scanpos is a string statement of row and column%         in the matrix formulation of a data scan.%      byteoffsets are byte positions as offsets from the %         first byte of a data scan.%   Function: return byte positions needed by 'dmx' to read%      the 1st format for a scan of raw data.%   M.Gregg, 16dec94.  %   4jul95: Modified for major changes to mmp1.  These also apply%          	to mmp2, which is identical to mmp1.  The new format%			also reads drops 1-2098, written with the old format.%  %  10jul96: Modified to add scanpos for subsampling a1 & a2 and%           reading a3,a4,vac1,vac2 in mmp3%  9/9/99:  modified by MHA to include mx, my and mz.%  The offset is (row-1)*20 + (col-1)*2 + 0 and 1.%10/7/00: modified to get sg right (config file had row6col2 which is %now hdg -- it is now row9col1. -mha.%7/13/01: modified to include new offsets for mmp2s configuration.if strcmp(scanid,'byteoffset1')==1	scanlen=320;	if strcmp(scanpos,'row1col1')		byteoffsets=[0 1];	elseif strcmp(scanpos,'row1col2')==1		byteoffsets=[2 3];	elseif strcmp(scanpos,'row13col2')==1	%mx		byteoffsets=[12*20+2 12*20+2+1];	elseif strcmp(scanpos,'row14col1')==1	%my		byteoffsets=[13*20 13*20+1];	elseif strcmp(scanpos,'row14col2')==1	%mz		byteoffsets=[13*20+2 13*20+2+1];	elseif strcmp(scanpos,'row21st3bytes')==1		byteoffsets=[20 21 22]; 	elseif strcmp(scanpos,'row9col1')==1	%sg, tld for mmp2s		byteoffsets=[160 161]; 	elseif strcmp(scanpos,'row9col2')==1	%trc for mmp2s		byteoffsets=[162 163]; 	elseif strcmp(scanpos,'row31st3bytes')==1		byteoffsets=[40 41 42]; 	elseif strcmp(scanpos,'row4col1')==1		byteoffsets=[60 61];	elseif strcmp(scanpos,'row4col2')==1		byteoffsets=[62 63];	elseif strcmp(scanpos,'row41st3bytes')==1		byteoffsets=[60 61 62];	elseif strcmp(scanpos,'row5col1')==1		byteoffsets=[80 81];			elseif strcmp(scanpos,'row5col2')==1		byteoffsets=[82 83];	elseif strcmp(scanpos,'row6col1')==1		byteoffsets=[100 101];	elseif strcmp(scanpos,'row6col2')==1		byteoffsets=[102 103];	elseif strcmp(scanpos,'col1rows7&15')==1 % a3 in mmp3, drops 2262-		byteoffsets=[120 121 280 281];		%i1 in mmp2s	elseif strcmp(scanpos,'col2rows7&15')==1 % a4 in mmp3, drops 2262-		byteoffsets=[122 123 282 283];		%i2 in mmp2s	elseif strcmp(scanpos,'col1rows8&16')==1 % vac1 in mmp3		byteoffsets=[140 141 300 301];		%i3 in mmp2s	elseif strcmp(scanpos,'col2rows8&16')==1 % vac2 in mmp3		byteoffsets=[142 143 302 303];		%i4 in mmp2s	elseif strcmp(scanpos,'col8rows7&15')==1 % a1 after drop 2261		byteoffsets=[134 135 294 295];	elseif strcmp(scanpos,'col9rows7&15')==1 % a2 after drop 2261		byteoffsets=[136 137 296 297];			elseif strcmp(scanpos,'col3')==1		byteoffsets=[4 5 24 25 44 45 64 65 84 85 104 105 124 ...		125 144 145 164 165 184 185 204 205 224 225 244 245 264 ...		265 284 285 304 305];	elseif strcmp(scanpos,'col4')==1		byteoffsets=[6 7 26 27 46 47 66 67 86 87 106 107 126 ...		127 146 147 166 167 186 187 206 207 226 227 246 247 266 ...		267 286 287 306 307];	elseif strcmp(scanpos,'col5')==1		byteoffsets=[8 9 28 29 48 49 68 69 88 89 108 109 128 ...		129 148 149 168 169 188 189 208 209 228 229 248 249 268 ...		269 288 289 308 309];	elseif strcmp(scanpos,'col6')==1		byteoffsets=[10 11 30 31 50 51 70 71 90 91 110 111 130 ...		131 150 151 170 171 190 191 210 211 230 231 250 251 270 ...		271 290 291 310 311];	elseif strcmp(scanpos,'col7')==1		byteoffsets=[12 13 32 33 52 53 72 73 92 93 112 113 132 ...		133 152 153 172 173 192 193 212 213 232 233 252 253 272 ...		273 292 293 312 313];	elseif strcmp(scanpos,'col8')==1		byteoffsets=[14 15 34 35 54 55 74 75 94 95 114 115 134 ...		135 154 155 174 175 194 195 214 215 234 235 254 255 274 ...		275 294 295 314 315];	elseif strcmp(scanpos,'col9')==1		byteoffsets=[16 17 36 37 56 57 76 77 96 97 116 117 136 ...		137 156 157 176 177 196 197 216 217 236 237 256 257 276 ...		277 296 297 316 317];	elseif strcmp(scanpos,'col10')==1		byteoffsets=[18 19 38 39 58 59 78 79 98 99 118 119 138 ...		139 158 159 178 179 198 199 218 219 238 239 258 259 278 ...		279 298 299 318 319];	elseif strcmp(scanpos,'col5col10')==1 %crg for mmp2s		byteoffsets=[8 9 18 19 28 29 38 39 48 49 58 59 68 69 78 79 ...		88 89 98 99 108 109 118 119 128 ...		129 138 139 148 149 158 159 168 169 178 179 188 189 198 199 ...		208 209 218 219 228 229 238 239 248 249 258 259 268 ...		269 278 279 288 289 298 299 308 309 318 319];	else	str=['  no byteoffsets found for scanid = ' scanid ...		      ' &  scanpos = ' scanpos];	disp(str)	byteoffsets=[]; scanlen=[];	end	elsestr=['  no byteoffsets found for scanid = ' scanid ...		      ' &  scanpos = ' scanpos];disp(str)byteoffsets=[]; scanlen=[];end