function fiber=get_fiber1_mmp(drop)%function fiber=get_fiber1_mmp(drop)%Get processed fiber-optic refractometer data.hfperscan=16;mmpfolderscruise=read_cruises_mmp(drop);mmpid=read_mmpid(drop);%load fiber filef_str=[procdata filesep cruise filesep 'fiber' filesep 'fiber' int2str(drop) '.mat'];if exist(f_str)==2  ld_str=['load ' setstr(39) f_str setstr(39)];  eval(ld_str)else  msg=['  get_fiber1_mmp: ' f_str ' not found'];	disp(msg)  breakend%form coupling ratiosfiber.as=fiber.i2./(fiber.i1+fiber.i2);fiber.ar=fiber.i4./(fiber.i3+fiber.i4);%Now form pressure vectors for i1-4 and crgfiber.pr_i=interp(fiber.pr,2);fiber.pr_crg=interp(fiber.pr,hfperscan*2);