function fiber=get_fiber2_mmp(drop)%function fiber=get_fiber2_mmp(drop)%Get processed fiber-optic refractometer data.%Modified and called get_fiber2_mmp to output voltages as well as intensities.%%5/02 MHA%mmpfolderscruise=read_cruises_mmp(drop);mmpid=read_mmpid(drop);FiberSetup=get_fibersetup_mmp(drop);%load fiber filef_str=[procdata filesep cruise filesep 'fiber' filesep 'fiber' int2str(drop) '.mat'];if exist(f_str)==2  ld_str=['load ' setstr(39) f_str setstr(39)];  eval(ld_str)else  msg=['  get_fiber1_mmp: ' f_str ' not found'];	disp(msg)  returnendfiber.i1v=fiber.i1*FiberSetup.S*FiberSetup.Rf_s;fiber.i2v=fiber.i2*FiberSetup.S*FiberSetup.Rf_s;fiber.i3v=fiber.i3*FiberSetup.S*FiberSetup.Rf_r;fiber.i4v=fiber.i4*FiberSetup.S*FiberSetup.Rf_r;%form coupling ratiosfiber.as=fiber.i2./(fiber.i1+fiber.i2);fiber.ar=fiber.i4./(fiber.i3+fiber.i4);%Now form pressure vectors for i1-4 and crgfiber.pr_i=interp(fiber.pr,2);fiber.pr_crg=interp(fiber.pr,FiberSetup.hfperscan*2);