function [C,D,T0]=read_Parospcal1_mmp(sensorid, calid)% read_Parospcal_mmp%   Usage: [C,D,T0]=read_Parospcal1_mmp(sensorid, calid);%     inputs%       sensorid is string with serial no for Paros gauge%       calid is string identifying which calibration to use%     outputs%         C,D,t0 are calibration parameters%   Function:%      Read calibration coef for Paros pressure gaugesmmpfolders;source_file=[mmpdatabase FSP 'cal' FSP 'pr' FSP sensorid];fid=fopen(source_file,'r');if (fid <= 2)	msg=['read_Parospcal1_mmp: file missing for sensorid=' sensorid ...	     ' and calid=' calid];	disp(msg)	test=0;else 	test=1;	while test==1		id=fscanf(fid,'%s',1);		C=fscanf(fid,'%s',1);		D=fscanf(fid,'%s',1);		T0=fscanf(fid,'%s',1);		if isempty(id)==1			msg=['read_csbe_cal: calid=' calid ' not found in ' ...			     'cal file for ' sensorid];			disp(msg)			break		elseif strcmp(id,calid)==1			test=2;			break		end	endendfclose(fid);if test==2	C=str2num(C); D=str2num(D); T0=str2num(T0);end