function [C1,C2,C3,D1,D2,T1,T2,T3,T4,T5]= ...   read_Parospcal2_mmp(sensorid, calid)% read_Parospcal_mmp%   Usage: [C1,C2,C3,D1,D2,T1,T2,T3,T4,T5]= ...%          read_Parospcal2_mmp(sensorid, calid);%     inputs%       sensorid is string with serial no for Paros gauge%       calid is string identifying which calibration to use%     outputs%         C,D,t0 are calibration parameters%   Function:%      Read calibration coef for Paros pressure gaugesmmpfolders;%source_file=[mmpdatabase '\cal\pr\' sensorid];source_file=[mmpdatabase filesep 'cal' filesep 'pr' filesep sensorid];fid=fopen(source_file,'r');if (fid <= 2)	msg=['read_Parospcal1_mmp: file missing for sensorid=' sensorid ...	     ' and calid=' calid];	disp(msg)	test=0;else 	test=1;	while test==1		id=fscanf(fid,'%s',1);		C1=fscanf(fid,'%s',1);		C2=fscanf(fid,'%s',1);		C3=fscanf(fid,'%s',1);		D1=fscanf(fid,'%s',1);		D2=fscanf(fid,'%s',1);		T1=fscanf(fid,'%s',1);		T2=fscanf(fid,'%s',1);		T3=fscanf(fid,'%s',1);		T4=fscanf(fid,'%s',1);		T5=fscanf(fid,'%s',1);		if isempty(id)==1			msg=['read_Parospcal1_mmp: calid=' calid ' not found in ' ...			     'cal file for ' sensorid];			disp(msg)			break		elseif strcmp(id,calid)==1			test=2;			break		end	endendfclose(fid);if test==2	C1=str2num(C1); C2=str2num(C2); C3=str2num(C3);	D1=str2num(D1); D2=str2num(D2); T1=str2num(T1);	T2=str2num(T2); T3=str2num(T3); T4=str2num(T4);	T5=str2num(T5);end