function [Sv,Cs]=read_af_cal(sensorid, calid)% read_af_cal('probe', 'calid')mmpfolders;source_file=[mmpdatabase filesep 'cal' filesep 'af' filesep sensorid];[fid,message]=fopen(source_file,'r');if (fid <= 2)	errstr=['source_file = ' source_file ' : ' message];	error(errstr);end test=0;while test==0	id=fscanf(fid,'%s',1);	Sv=fscanf(fid,'%s',1);	Cs=fscanf(fid,'%s',1);	if id==0		error('      calid not found')	elseif strcmp(id,calid)==1		test=1;		Sv=str2num(Sv); Cs=str2num(Cs);		break	else		Sv=[]; Cs=[];	endend		fclose(fid);