function [sensorid,electronicsid,filter,fc,scanpos]= read_chconfig_mmp(ch,mmpid,drop)% read_chconfig_mmp:%   Usage: [sensorid,electronicsid,filter,fc,scanpos] ...%      = read_chconfig_mmp(ch,mmpid,drop)%         ch is string name of data channel%         mmpid is string name of vehicle%         drop is integer number of drop%         sensorid is string designation of sensor%         electronics id is string designation of electronics%         filter is string name of filter, e.g. bessel4%         fc is string value of filter cut-off frequency%         scanpos is string name of byte positions for channel%   Function: Return config parameters or nulls if the parameters%     are [] in the configuration file. % determine which config file to readmmpfolders;%global FSPchconfig_file=[mmpdatabase filesep 'config' filesep mmpid filesep ch];[fid,message]=fopen(chconfig_file,'r');if (fid <= 2)	msg=['  read_chconfig_mmp: ' chconfig_file ' not found'];	disp(msg)	returnendtest=0;while test==0	% Read a line in the config file	firstdrop=fscanf(fid,'%s',1);	lastdrop=fscanf(fid,'%s',1);	sensorid=fscanf(fid,'%s',1);	electronicsid=fscanf(fid,'%s',1);	filter=fscanf(fid,'%s',1);	fc=fscanf(fid,'%s',1);	scanpos=fscanf(fid,'%s',1);	if isempty(firstdrop)		msg=['  read_chconfig_mmp: drop ' int2str(drop) ' not ' ...		'in config\' mmpid '\' ch];		disp(msg);		return;	else		firstdrop=str2num(firstdrop);		lastdrop=str2num(lastdrop);	end	if drop >= firstdrop & drop <= lastdrop		test=1;	endendfclose(fid);% Set outputs to nulls if drop was not found between first and% last drop.  Also set to nulls any values that are read as% two brackets.if test~=1	sensorid=[]; electronicsid=[]; filter=[]; fc=[]; scanpos=[];else	if strcmp(sensorid,'[]') sensorid=[]; end	if strcmp(electronicsid,'[]') electronicsid=[]; end	if strcmp(filter,'[]') filter=[]; end	if strcmp(fc,'[]')	  fc=[];	else	  fc=str2num(fc);	end	if strcmp(scanpos,'[]')  scanpos=[]; endend