function [scanid,voffsetid] = read_config_mmp(mmpid,drop)% read_config_mmp:%   Usage: [scanid,voffsetid] = read_config_mmp(mmpid,drop)%   Function: Returns scanid and voffsetid for mmp vehicle & drop % determine which config file to readmmpfolders;config_file=[mmpdatabase filesep 'config' filesep mmpid filesep 'config'];if exist(config_file)~=2  disp_str=['  read_config_mmp: config file does not exist'];  disp(disp_str)else   [fid,message]=fopen(config_file,'r');  if (fid <= 2)	 errstr=['config_file = ' config_file ' : ' message];	 error(errstr);  end  test=0;  while test==0	% Read a line in the config file	firstdrop=fscanf(fid,'%s',1);	lastdrop=fscanf(fid,'%s',1);	scanid=fscanf(fid,'%s',1);	voffsetid=fscanf(fid,'%s',1);	if isempty(firstdrop)		msg=['read_config_mmp: drop ' int2str(drop) ' not ' ...		'found for ' mmpid];		disp(msg);		break;	else		firstdrop=str2num(firstdrop);		lastdrop=str2num(lastdrop);	end	if drop >= firstdrop & drop <= lastdrop		test=1;	end  end  fclose(fid);  if test~=1	scanid=[]; voffsetid=[];  endend