function cruise=read_cruises_mmp(drop)% read_cruises_mmp%   Usage: cruise=read_cruises_mmp(drop)%      drop is sequential drop number for mmp family%   Function: return cruise name for specified drop number% Get folder name for config and cal filesmmpfolders;% Read channel config filesource_file=[mmpdatabase filesep 'cruises'];[fid,message]=fopen(source_file,'r');if (fid <= 2)	errstr=['source_file = ' source_file ' : ' message];	error(errstr);endtest=0;while test==0	% Read a line in the config file	firstdrop=fscanf(fid,'%s',1);	lastdrop=fscanf(fid,'%s',1);	cruise=fscanf(fid,'%s',1);	if isempty(firstdrop)		errstr=['      cruise not found for drop ' num2str(drop) ...		   '; check ' source_file];		error(errstr)	else		firstdrop=str2num(firstdrop);		lastdrop=str2num(lastdrop);	end	if drop >= firstdrop & drop <= lastdrop		test=1;	endendfclose(fid);if test~=1	case_config=[];end