function magnetic_variation=read_magvar_mmp(cruise)% Usage: magnetic_variation=read_magvar_mmp(cruise);%   Function: Reads magnetic variationmmpfolders;source_file=[mmpdatabase filesep 'cal' filesep 'magnetic_variation'];fid=fopen(source_file,'r');if (fid <= 2)	msg=['read_magvar_mmp: file missing for cruise=' cruise];	disp(msg)	test=0;else 	test=1;	while test==1		cr=fscanf(fid,'%s',1);		mv=fscanf(fid,'%s',1);		if isempty(cr)==1			msg=['read_magvar_mmp: cruise=' cruise ' not found in ' ...			     source_file];			disp(msg)		elseif strcmp(cr,cruise)==1			test=2;		end	endendfclose(fid);if test==2	magnetic_variation=str2num(mv); end