function mmpid=read_mmpid(drop)% read_mmpid%   Usage: mmpid=read_mmpid(drop);%      drop is sequential drop number for mmp family%   Function: read MMP:config:mmpid to get mmp vehicle used for drop% Get folder name for mmp data basemmpfolders;% Read channel config filemmpid_file=[mmpdatabase filesep 'config' filesep 'mmpid'];[fid,message]=fopen(mmpid_file,'r');if (fid <= 2)	errstr=['mmpid_file = ' mmpid_file ' : ' message];	error(errstr);endtest=0;while test==0	% Read a line in the config file	firstdrop=fscanf(fid,'%s',1);	lastdrop=fscanf(fid,'%s',1);	mmpid=fscanf(fid,'%s',1);	if isempty(firstdrop)		msg=['read_mmpid: drop ' int2str(drop) ' not found'];		disp(msg);		break;	else		firstdrop=str2num(firstdrop);		lastdrop=str2num(lastdrop);	end	if drop >= firstdrop & drop <= lastdrop		test=1;	endendfclose(fid);if test~=1	mmpid=[];end