function Q = read_pump_mmp(mmpid,drop)% read_pump_mmp:%   Usage: Q = read_pump_mmp(mmid,drop)%      drop is the drop number%      Q is the flow rate, in liters/s%   Function: reads pump flow rate,Q mmpfolders;% determine which config file to readpump_file=[mmpdatabase filesep 'config' filesep mmpid filesep 'pump'];[fid,message]=fopen(pump_file,'r');if (fid <= 2)	errstr=['pump_file = ' pump_file filesep message];	error(errstr);endtest=0;while test==0	% Read a line in the pump file	firstdrop=fscanf(fid,'%s',1);	lastdrop=fscanf(fid,'%s',1);	pumpid=fscanf(fid,'%s',1);	Q=fscanf(fid,'%s',1);	if isempty(firstdrop)		msg=[' read_pump_mmp: pump info not found for ' ...		      'mmpid = ' mmpid ' and drop=' drop];		disp(msg);		break;	else		firstdrop=str2num(firstdrop);		lastdrop=str2num(lastdrop);	end	if drop >= firstdrop & drop <= lastdrop		test=1;	endendfclose(fid);if test==1	Q=str2num(Q);else	Q=NaN;end