function calid=read_whichcal_mmp(ch,sensorid,drop)% read_whichcal_mmp%   Usage: calid=read_whichcal_mmp(ch,sensorid,drop)%      ch is the string naming the data channel.  Use 'af' for%        either airfoil channel%      sensorid is the string name of the probe%      drop is the sequential drop number%      calid is the string naming the calibration%   Function: reads calibid for sensorid and dropif strcmp(sensorid,'[]')==0	% Get folder name for config and cal files	mmpfolders;	% Read wichcal file for ch & sensorid	whichcal_file=[mmpdatabase filesep 'cal' filesep ch filesep 'whichcal_' sensorid]; 	fid=fopen(whichcal_file,'r');	if (fid <= 2)		msg=['read_whichcal_mmp: file missing for ch=' ch ...	       ' and sensorid=' sensorid];		disp(msg)		test=0;	else		test=1;		while test==1			% Read a line in whichcal file			firstdrop=fscanf(fid,'%s',1);			lastdrop=fscanf(fid,'%s',1);			calid=fscanf(fid,'%s',1);			if isempty(firstdrop)==1				msg1=['read_whichcal_mmp: drop=' num2str(drop) ...			      ' not found in ' whichcal_file];				disp(msg1)				test=0;				break			else				firstdrop=str2num(firstdrop); 				lastdrop=str2num(lastdrop);				if drop >= firstdrop & drop <= lastdrop					test=2;					break				end			end		end	end	fclose(fid);end