function [chi, pr_chi]=get_chi1_mmp(drop);% Usage: function [chi, pr_chi]=get_chi1_mmp(drop);%   drop is the integer drop number%   pr_eps is the pressure at the center of the epsilon window.%mmpfolderscruise=read_cruises_mmp(drop);% set up string names of files to openfstr1=[procdata filesep cruise filesep 'chi' filesep 'chi' int2str(drop) '.mat'];% load eps<drop>if exist(fstr1)~=2	nodata_str=['get_chi1_mmp: ' fstr1 ' does not exist'];	disp(nodata_str)else	ld_str=['load ' setstr(39) fstr1 setstr(39)];	eval(ld_str)end